import { SEARCH_DATA, STORE_DATA } from "../ActionTypes/actiontype";
const init = {
  data: [],
  search: "",
};
const reducer = (state = init, {type,payload}) => {
  switch (type) {
    case STORE_DATA:
      return { ...state, data: payload };
    case SEARCH_DATA:
      return { ...state, search: payload };
      case ADD_CART:
          var newCart = [...store.cart, payload];
          return { ...store, cart: newCart };
    
        case DELETE_CART:
          var xn = store.cart.filter((e) => {
            if (e.id !== payload) {
              return e;
            }
          });  
          return { ...store, cart: [...xn] };
        case REMOVE_ALL:
          return { ...store, cart: [] };
    default:
      return state;
  }
};
export { reducer };
