import {
  ADD_CART,
  DELETE_CART,
  REMOVE_ALL,
  SEARCH_DATA,
  STORE_DATA,
} from "../ActionTypes/actiontype";
const init = {
  data: [],
  search: "",
  cart: [],
};
const reducer = (state = init, { type, payload }) => {
  switch (type) {
    case STORE_DATA:
      return { ...state, data: payload };
    case SEARCH_DATA:
      return { ...state, search: payload };
    //
    default:
      return state;
  }
};
export { reducer };
