            {x.map((e) => {
              return (
                <div>
                  <div>
                    <img src={e.img} />
                  </div>
                  <div>
                    <p> {e.name}</p>
                    <p> {e.Brand}</p>
                    <p> {e.category}</p>
                    <p> {e.price}</p>
